INSERT INTO `project` VALUES (1,4,'Blaze Romance Novel','Novel','Romance','A woman on the run for a crime she committed in self defense against her abusive husband takes refuge with her cousin whose husband happens to be the leader of a motorcycle gang that includes a couple of handsome outlaws of its own...','2015-12-11 13:25:17','2016-05-07 02:58:08','bookproject',NULL),(30,4,'Daily Writings','Journal','Non Fiction','My Journal','2016-04-01 13:43:25','2016-05-19 06:02:34','journalproject',NULL),(33,5,'My Diary','Journal','Non Fiction','Daily thoughts...\r\nDiary entries','2016-04-29 19:14:22','2016-05-16 16:30:48','journalproject',NULL),(34,5,'Moving In','Short Story','Suspense / Thriller','You move into a new house but something seems amiss','2016-04-29 19:34:04','2016-05-14 02:53:58','storyproject',NULL),(36,4,'Big Data Little People','Novel','Realistic Fiction','A rough autobiography turned into a story about a dystopian future.','2016-05-02 01:14:23','2016-06-26 03:44:20','bookproject',NULL),(41,5,'Washington','ScreenPlay','Horror','A group of teens decides to take a post graduation camping trip in the woods. \r\nHorror ensues.','2016-05-14 02:38:54','2016-05-14 02:46:17','playproject',NULL),(42,5,'How did it come to this','Novel','Comedy','A struggling artist is met with a proposition he can\'t refuse...','2016-05-14 02:56:17','2016-05-14 03:14:54','bookproject',NULL);
